"use client";
import { getStudentCourses } from "@/features/Courses/courseSlice";
import { BookOutlined, FolderOpenOutlined } from "@ant-design/icons";
import { unwrapResult } from "@reduxjs/toolkit";
import { Breadcrumb, Image, Spin } from "antd";
import Link from "next/link";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

export default function Course() {
  const dispatch = useDispatch();
  const [course, setCourse] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  //viewCourses api
  useEffect(() => {
    setIsLoading(true);
    dispatch(getStudentCourses())
      .then(unwrapResult)
      .then((res) => {
        if (res.status) {
          setCourse(res.metadata);
        }
        setIsLoading(false);
      })
      .catch((error) => {
        setIsLoading(false);
      });
  }, [dispatch]);

  return (
    <div className="mx-auto px-4 sm:px-6 lg:px-8">
      <Breadcrumb className="pt-3 pl-5">
        <Breadcrumb.Item>
          <Link href="/">Trang chủ</Link>
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <Link href="/courses/view-course">
            <span className="font-medium text-gray-700">Khóa học của tôi</span>
          </Link>
        </Breadcrumb.Item>
      </Breadcrumb>
      <h1 className="mt-2 pl-5 text-3xl font-extrabold tracking-tight text-gray-900">
        Khóa học của tôi
      </h1>

      <div className="flex p-4">
        <div className="content flex-1">
          {isLoading ? (
            <div className="flex justify-center items-center h-screen">
              <Spin />
            </div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-4 gap-4 pt-3 pb-28">
              {course &&
                course.map((item, index) => (
                  <Link href={`/courses/lessons/${item?._id}`} key={index}>
                    <div className="group hover:shadow-sm transition overflow-hidden border rounded-lg p-3 min-h-[100px]">
                      <div className="relative w-full aspect-video rounded-md overflow-hidden">
                        <Image
                          width={380}
                          height={186}
                          className="object-cover"
                          alt="course image"
                          fallback="data:image/png;base64,"
                          src={item?.image_url}
                        />
                      </div>
                      <div className="flex flex-col pt-2">
                        <div className="text-lg md:text-base font-medium group-hover:text-sky-700 transition line-clamp-2">
                          {item.name}
                        </div>
                        <div className="text-xs text-[#6B7280] font-normal md:text-sm group-hover:text-sky-700 transition line-clamp-1">
                          Mô tả: {item.title}
                        </div>
                        <p className="text-xs font-bold text-muted-foreground">
                          Giáo viên: {item.teacher?.lastName}
                        </p>
                        <div className="my-3 flex items-center gap-x-2 text-sm md:text-xs">
                          <div className="flex items-center gap-x-1 text-slate-500">
                            <BookOutlined />
                            <span>lessons: {item.lessons.length}</span>
                          </div>
                          <div className="flex items-center gap-x-1 text-slate-500"></div>
                        </div>
                      </div>
                    </div>
                  </Link>
                ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
